import Header from "./components/Header";
import Footer from "./components/Footer";
import React, { useState } from "react";
function App() {

  const [weight, setWeight] = useState(0);
  const [hight, setHight] = useState(0);
  const [bmi, setBmi] = useState('');
  const [massege, setMassege] = useState('');

let calcbmi=(e)=>{

  e.preventDefault();
if(weight===0||hight===0){
  alert("please enter a valid weight and height")
}
else{
  let bmi=((703*weight)/(hight*hight));
  setBmi(bmi.toFixed(1))



if(bmi<25){
  setMassege("you are underweight")
}
else if(bmi>=25 && bmi<30)
{
  setMassege('you are a healthy')
}

else{
  setMassege('you are overweight') 
}

}
}

return (

    <div className="App">

      <dic className="container">

        <h2> BMI Calculor </h2>
        <form>
          <div>
            <label>Weight (lbs)</label>
            <input type="text" placeholder="Enter weight value " onChange={(e)=>setWeight(e.target.value)} value={weight} />

          </div>



          <div>
            <label>Height (lbs)</label>
            <input type="text" placeholder="Enter weight value "onChange={(e)=>setHight(e.target.value)} value={hight} />

          </div>





          <div>
            <button className="btn" type="submit" onClick={calcbmi}>submit</button>
            <button className="btn" type="submit" >Reload</button>

            <div className="center">
              <h1> Your BMI is{bmi}</h1>
              <p>{massege}</p>
            </div>


          </div>
        </form>

      </dic>

    </div>



  );
}

export default App;
